import React from 'react';

const Register = () => {
  return (
    <div className="register-page">
      <div className="hero">
        <h1>üìù Register</h1>
        <p>Create your LitVerse account</p>
      </div>
      
      <div className="card" style={{ padding: '2rem', textAlign: 'center', maxWidth: '400px', margin: '0 auto' }}>
        <h2>Registration Form</h2>
        <p>This page will include:</p>
        <ul style={{ textAlign: 'left' }}>
          <li>Name input</li>
          <li>Email input</li>
          <li>Password input</li>
          <li>Confirm password</li>
          <li>Terms and conditions</li>
          <li>Sign in link</li>
        </ul>
      </div>
    </div>
  );
};

export default Register;
