import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';

const Books = () => {
  const [books, setBooks] = useState([]);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedGenre, setSelectedGenre] = useState('all');
  const [sortBy, setSortBy] = useState('title');

  const genres = [
    'All', 'Fiction', 'Non-Fiction', 'Mystery', 'Romance', 'Sci-Fi', 
    'Fantasy', 'Biography', 'History', 'Self-Help', 'Business'
  ];

  useEffect(() => {
    // Simulate loading books
    setTimeout(() => {
      setBooks([
        {
          id: 1,
          title: "The Great Gatsby",
          author: "F. Scott Fitzgerald",
          price: 12.99,
          originalPrice: 15.99,
          rating: 4.5,
          reviews: 1247,
          genre: "Fiction",
          cover: "📚",
          inStock: true,
          bestseller: true
        },
        {
          id: 2,
          title: "1984",
          author: "George Orwell",
          price: 9.99,
          originalPrice: 12.99,
          rating: 4.7,
          reviews: 892,
          genre: "Fiction",
          cover: "📖",
          inStock: true,
          bestseller: true
        },
        {
          id: 3,
          title: "To Kill a Mockingbird",
          author: "Harper Lee",
          price: 11.99,
          originalPrice: 14.99,
          rating: 4.8,
          reviews: 1563,
          genre: "Fiction",
          cover: "📘",
          inStock: true,
          bestseller: false
        },
        {
          id: 4,
          title: "The Hobbit",
          author: "J.R.R. Tolkien",
          price: 14.99,
          originalPrice: 18.99,
          rating: 4.6,
          reviews: 2341,
          genre: "Fantasy",
          cover: "📙",
          inStock: true,
          bestseller: true
        },
        {
          id: 5,
          title: "Pride and Prejudice",
          author: "Jane Austen",
          price: 8.99,
          originalPrice: 11.99,
          rating: 4.4,
          reviews: 987,
          genre: "Romance",
          cover: "📗",
          inStock: false,
          bestseller: false
        },
        {
          id: 6,
          title: "The Catcher in the Rye",
          author: "J.D. Salinger",
          price: 10.99,
          originalPrice: 13.99,
          rating: 4.3,
          reviews: 756,
          genre: "Fiction",
          cover: "📕",
          inStock: true,
          bestseller: false
        },
        {
          id: 7,
          title: "Dune",
          author: "Frank Herbert",
          price: 16.99,
          originalPrice: 19.99,
          rating: 4.9,
          reviews: 3421,
          genre: "Sci-Fi",
          cover: "📓",
          inStock: true,
          bestseller: true
        },
        {
          id: 8,
          title: "The Alchemist",
          author: "Paulo Coelho",
          price: 13.99,
          originalPrice: 16.99,
          rating: 4.2,
          reviews: 2345,
          genre: "Self-Help",
          cover: "📔",
          inStock: true,
          bestseller: true
        }
      ]);
      setLoading(false);
    }, 1000);
  }, []);

  const filteredBooks = books.filter(book => {
    const matchesSearch = book.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         book.author.toLowerCase().includes(searchTerm.toLowerCase());
    const matchesGenre = selectedGenre === 'all' || book.genre === selectedGenre;
    return matchesSearch && matchesGenre;
  });

  const sortedBooks = [...filteredBooks].sort((a, b) => {
    switch (sortBy) {
      case 'title':
        return a.title.localeCompare(b.title);
      case 'author':
        return a.author.localeCompare(b.author);
      case 'price':
        return a.price - b.price;
      case 'rating':
        return b.rating - a.rating;
      default:
        return 0;
    }
  });

  const addToCart = (bookId) => {
    // Simulate adding to cart
    console.log(`Added book ${bookId} to cart`);
    alert('Book added to cart!');
  };

  return (
    <div className="books-page">
      <div className="hero">
        <h1>📚 Discover Your Next Favorite Book</h1>
        <p>Explore our vast collection of books with AI-powered recommendations</p>
      </div>

      {/* Search and Filters */}
      <div className="card" style={{ marginBottom: '2rem', padding: '1.5rem' }}>
        <div className="grid grid-3" style={{ gap: '1rem', alignItems: 'end' }}>
          <div>
            <label style={{ display: 'block', marginBottom: '0.5rem', fontWeight: 'bold' }}>
              Search Books
            </label>
            <input
              type="text"
              placeholder="Search by title or author..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              style={{
                width: '100%',
                padding: '0.75rem',
                border: '2px solid #e9ecef',
                borderRadius: '8px',
                fontSize: '1rem'
              }}
            />
          </div>
          
          <div>
            <label style={{ display: 'block', marginBottom: '0.5rem', fontWeight: 'bold' }}>
              Genre
            </label>
            <select
              value={selectedGenre}
              onChange={(e) => setSelectedGenre(e.target.value)}
              style={{
                width: '100%',
                padding: '0.75rem',
                border: '2px solid #e9ecef',
                borderRadius: '8px',
                fontSize: '1rem'
              }}
            >
              {genres.map(genre => (
                <option key={genre} value={genre === 'All' ? 'all' : genre}>
                  {genre}
                </option>
              ))}
            </select>
          </div>
          
          <div>
            <label style={{ display: 'block', marginBottom: '0.5rem', fontWeight: 'bold' }}>
              Sort By
            </label>
            <select
              value={sortBy}
              onChange={(e) => setSortBy(e.target.value)}
              style={{
                width: '100%',
                padding: '0.75rem',
                border: '2px solid #e9ecef',
                borderRadius: '8px',
                fontSize: '1rem'
              }}
            >
              <option value="title">Title</option>
              <option value="author">Author</option>
              <option value="price">Price</option>
              <option value="rating">Rating</option>
            </select>
          </div>
        </div>
      </div>

      {/* Results Count */}
      <div style={{ marginBottom: '1rem', color: '#666' }}>
        Showing {sortedBooks.length} of {books.length} books
      </div>

      {/* Books Grid */}
      {loading ? (
        <div className="spinner"></div>
      ) : (
        <div className="grid grid-4">
          {sortedBooks.map(book => (
            <div key={book.id} className="book-card">
              <div className="book-image">
                {book.cover}
                {book.bestseller && (
                  <div style={{
                    position: 'absolute',
                    top: '10px',
                    left: '10px',
                    background: '#ffc107',
                    color: '#333',
                    padding: '0.25rem 0.5rem',
                    borderRadius: '4px',
                    fontSize: '0.8rem',
                    fontWeight: 'bold'
                  }}>
                    BESTSELLER
                  </div>
                )}
                {!book.inStock && (
                  <div style={{
                    position: 'absolute',
                    top: '10px',
                    right: '10px',
                    background: '#dc3545',
                    color: 'white',
                    padding: '0.25rem 0.5rem',
                    borderRadius: '4px',
                    fontSize: '0.8rem',
                    fontWeight: 'bold'
                  }}>
                    OUT OF STOCK
                  </div>
                )}
              </div>
              <div className="book-info">
                <div className="book-title">{book.title}</div>
                <div className="book-author">by {book.author}</div>
                <div style={{ marginBottom: '1rem' }}>
                  <span style={{ color: '#ffc107' }}>★</span>
                  <span style={{ marginLeft: '0.5rem' }}>{book.rating}</span>
                  <span style={{ color: '#666', marginLeft: '0.5rem' }}>({book.reviews} reviews)</span>
                </div>
                <div style={{ marginBottom: '1rem' }}>
                  <span style={{ color: '#666', fontSize: '0.9rem' }}>{book.genre}</span>
                </div>
                <div style={{ marginBottom: '1rem' }}>
                  <span className="book-price">${book.price}</span>
                  {book.originalPrice > book.price && (
                    <span style={{ 
                      textDecoration: 'line-through', 
                      color: '#666', 
                      marginLeft: '0.5rem',
                      fontSize: '0.9rem'
                    }}>
                      ${book.originalPrice}
                    </span>
                  )}
                </div>
                <div style={{ display: 'flex', gap: '0.5rem' }}>
                  <Link to={`/books/${book.id}`} className="btn btn-secondary" style={{ flex: 1 }}>
                    Details
                  </Link>
                  <button 
                    className="btn btn-primary" 
                    style={{ flex: 1 }}
                    onClick={() => addToCart(book.id)}
                    disabled={!book.inStock}
                  >
                    {book.inStock ? 'Add to Cart' : 'Out of Stock'}
                  </button>
                </div>
              </div>
            </div>
          ))}
        </div>
      )}

      {/* AI Recommendations */}
      <div className="card" style={{ marginTop: '3rem', padding: '2rem' }}>
        <h3 style={{ marginBottom: '1rem', textAlign: 'center' }}>
          🤖 AI-Powered Recommendations
        </h3>
        <p style={{ textAlign: 'center', color: '#666', marginBottom: '2rem' }}>
          Based on your reading history and preferences, here are some books you might enjoy:
        </p>
        <div className="grid grid-3">
          <div className="feature-card">
            <div style={{ fontSize: '2rem', marginBottom: '1rem' }}>🎯</div>
            <h4>Personalized Picks</h4>
            <p>Books tailored to your reading preferences and mood</p>
          </div>
          <div className="feature-card">
            <div style={{ fontSize: '2rem', marginBottom: '1rem' }}>📈</div>
            <h4>Trending Now</h4>
            <p>Discover what's popular among readers like you</p>
          </div>
          <div className="feature-card">
            <div style={{ fontSize: '2rem', marginBottom: '1rem' }}>🔍</div>
            <h4>Smart Discovery</h4>
            <p>Find hidden gems based on your favorite authors and genres</p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Books;
