import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';

const Home = () => {
  const [featuredBooks, setFeaturedBooks] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Simulate loading featured books
    setTimeout(() => {
      setFeaturedBooks([
        {
          id: 1,
          title: "The Great Gatsby",
          author: "F. Scott Fitzgerald",
          price: 12.99,
          rating: 4.5,
          cover: "üìö",
          genre: "Classic"
        },
        {
          id: 2,
          title: "1984",
          author: "George Orwell",
          price: 9.99,
          rating: 4.7,
          cover: "üìñ",
          genre: "Dystopian"
        },
        {
          id: 3,
          title: "To Kill a Mockingbird",
          author: "Harper Lee",
          price: 11.99,
          rating: 4.8,
          cover: "üìò",
          genre: "Classic"
        },
        {
          id: 4,
          title: "The Hobbit",
          author: "J.R.R. Tolkien",
          price: 14.99,
          rating: 4.6,
          cover: "üìô",
          genre: "Fantasy"
        }
      ]);
      setLoading(false);
    }, 1000);
  }, []);

  const features = [
    {
      icon: "ü§ñ",
      title: "AI-Powered Reading Journey",
      description: "Track your reading progress with intelligent insights and personalized recommendations based on your reading patterns."
    },
    {
      icon: "üë•",
      title: "Social Reading Revolution",
      description: "Join book clubs, share reading experiences, and connect with fellow readers around the world."
    },
    {
      icon: "üéÆ",
      title: "Gamified Learning",
      description: "Earn achievements, badges, and points as you read. Complete challenges and climb the leaderboard."
    },
    {
      icon: "üîç",
      title: "Smart Book Discovery",
      description: "Discover your next favorite book with AI-powered recommendations based on your mood and preferences."
    },
    {
      icon: "üìö",
      title: "Virtual 3D Bookshelf",
      description: "Organize your digital library in a beautiful 3D virtual bookshelf that you can explore and customize."
    },
    {
      icon: "üéØ",
      title: "Reading Analytics",
      description: "Get detailed insights into your reading habits, speed, and comprehension with advanced analytics."
    }
  ];

  const stats = [
    { number: "10,000+", label: "Books Available" },
    { number: "50,000+", label: "Active Readers" },
    { number: "1,000+", label: "Book Clubs" },
    { number: "95%", label: "Satisfaction Rate" }
  ];

  return (
    <div className="home">
      {/* Hero Section */}
      <section className="hero fade-in-up">
        <h1>Welcome to LitVerse</h1>
        <p>Beyond the Bookstore - Where Reading Meets Innovation</p>
        <div style={{ marginTop: '2rem' }}>
          <Link to="/books" className="btn btn-primary" style={{ marginRight: '1rem' }}>
            Explore Books
          </Link>
          <Link to="/virtual-bookshelf" className="btn btn-secondary">
            Virtual Bookshelf
          </Link>
        </div>
      </section>

      {/* Stats Section */}
      <section className="stats-section" style={{ marginBottom: '3rem' }}>
        <div className="grid grid-4">
          {stats.map((stat, index) => (
            <div key={index} className="stat-card card" style={{ textAlign: 'center', padding: '2rem' }}>
              <div style={{ fontSize: '2.5rem', fontWeight: 'bold', color: '#667eea', marginBottom: '0.5rem' }}>
                {stat.number}
              </div>
              <div style={{ color: '#666' }}>{stat.label}</div>
            </div>
          ))}
        </div>
      </section>

      {/* Featured Books */}
      <section style={{ marginBottom: '3rem' }}>
        <h2 style={{ textAlign: 'center', marginBottom: '2rem', color: '#333' }}>
          Featured Books
        </h2>
        {loading ? (
          <div className="spinner"></div>
        ) : (
          <div className="grid grid-4">
            {featuredBooks.map((book) => (
              <div key={book.id} className="book-card">
                <div className="book-image">
                  {book.cover}
                </div>
                <div className="book-info">
                  <div className="book-title">{book.title}</div>
                  <div className="book-author">by {book.author}</div>
                  <div style={{ marginBottom: '1rem' }}>
                    <span style={{ color: '#ffc107' }}>‚òÖ</span>
                    <span style={{ marginLeft: '0.5rem' }}>{book.rating}</span>
                    <span style={{ color: '#666', marginLeft: '0.5rem' }}>({book.genre})</span>
                  </div>
                  <div className="book-price">${book.price}</div>
                  <Link to={`/books/${book.id}`} className="btn btn-primary" style={{ width: '100%' }}>
                    View Details
                  </Link>
                </div>
              </div>
            ))}
          </div>
        )}
      </section>

      {/* Features Section */}
      <section style={{ marginBottom: '3rem' }}>
        <h2 style={{ textAlign: 'center', marginBottom: '2rem', color: '#333' }}>
          Revolutionary Features
        </h2>
        <div className="features">
          {features.map((feature, index) => (
            <div key={index} className="feature-card">
              <div className="feature-icon">{feature.icon}</div>
              <div className="feature-title">{feature.title}</div>
              <div className="feature-description">{feature.description}</div>
            </div>
          ))}
        </div>
      </section>

      {/* Call to Action */}
      <section className="card" style={{ textAlign: 'center', padding: '3rem', marginBottom: '3rem' }}>
        <h2 style={{ marginBottom: '1rem', color: '#333' }}>
          Ready to Start Your Reading Journey?
        </h2>
        <p style={{ marginBottom: '2rem', color: '#666' }}>
          Join thousands of readers who are already experiencing the future of reading with LitVerse.
        </p>
        <div>
          <Link to="/register" className="btn btn-primary" style={{ marginRight: '1rem' }}>
            Get Started
          </Link>
          <Link to="/book-clubs" className="btn btn-secondary">
            Join a Book Club
          </Link>
        </div>
      </section>

      {/* Quick Actions */}
      <section style={{ marginBottom: '3rem' }}>
        <h2 style={{ textAlign: 'center', marginBottom: '2rem', color: '#333' }}>
          Quick Actions
        </h2>
        <div className="grid grid-3">
          <Link to="/reading-journey" className="card" style={{ padding: '2rem', textAlign: 'center', textDecoration: 'none', color: 'inherit' }}>
