import React from 'react';

const Login = () => {
  return (
    <div className="login-page">
      <div className="hero">
        <h1>üîê Login</h1>
        <p>Sign in to your LitVerse account</p>
      </div>
      
      <div className="card" style={{ padding: '2rem', textAlign: 'center', maxWidth: '400px', margin: '0 auto' }}>
        <h2>Login Form</h2>
        <p>This page will include:</p>
        <ul style={{ textAlign: 'left' }}>
          <li>Email/username input</li>
          <li>Password input</li>
          <li>Remember me option</li>
          <li>Forgot password link</li>
          <li>Sign up link</li>
        </ul>
      </div>
    </div>
  );
};

export default Login;
