import React, { useState } from 'react';
import { Link } from 'react-router-dom';

const VirtualBookshelf = () => {
  const [selectedShelf, setSelectedShelf] = useState('all');
  const [viewMode, setViewMode] = useState('grid');

  const bookshelves = [
    {
      id: 'favorites',
      name: 'Favorites',
      icon: '❤️',
      count: 12
    },
    {
      id: 'currently-reading',
      name: 'Currently Reading',
      icon: '📖',
      count: 3
    },
    {
      id: 'to-read',
      name: 'To Read',
      icon: '📚',
      count: 25
    },
    {
      id: 'completed',
      name: 'Completed',
      icon: '✅',
      count: 47
    },
    {
      id: 'wishlist',
      name: 'Wishlist',
      icon: '🎁',
      count: 18
    }
  ];

  const books = [
    {
      id: 1,
      title: "The Great Gatsby",
      author: "F. Scott Fitzgerald",
      shelf: "favorites",
      progress: 100,
      cover: "📚",
      rating: 5
    },
    {
      id: 2,
      title: "1984",
      author: "George Orwell",
      shelf: "currently-reading",
      progress: 65,
      cover: "📖",
      rating: 4
    },
    {
      id: 3,
      title: "To Kill a Mockingbird",
      author: "Harper Lee",
      shelf: "completed",
      progress: 100,
      cover: "📘",
      rating: 5
    },
    {
      id: 4,
      title: "The Hobbit",
      author: "J.R.R. Tolkien",
      shelf: "to-read",
      progress: 0,
      cover: "📙",
      rating: 0
    },
    {
      id: 5,
      title: "Pride and Prejudice",
      author: "Jane Austen",
      shelf: "wishlist",
      progress: 0,
      cover: "📗",
      rating: 0
    },
    {
      id: 6,
      title: "The Catcher in the Rye",
      author: "J.D. Salinger",
      shelf: "completed",
      progress: 100,
      cover: "📕",
      rating: 4
    }
  ];

  const filteredBooks = selectedShelf === 'all' 
    ? books 
    : books.filter(book => book.shelf === selectedShelf);

  const getProgressColor = (progress) => {
    if (progress === 100) return '#28a745';
    if (progress > 50) return '#ffc107';
    return '#dc3545';
  };

  return (
    <div className="virtual-bookshelf-page">
      <div className="hero">
        <h1>📚 Virtual Bookshelf</h1>
        <p>Organize your digital library in a beautiful 3D space</p>
      </div>

      {/* Bookshelf Navigation */}
      <div className="bookshelf-nav card" style={{ marginBottom: '2rem', padding: '1.5rem' }}>
        <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '1rem' }}>
          <h3 style={{ margin: 0 }}>My Bookshelves</h3>
          <div>
            <button 
              className={`btn ${viewMode === 'grid' ? 'btn-primary' : 'btn-secondary'}`}
              onClick={() => setViewMode('grid')}
              style={{ marginRight: '0.5rem' }}
            >
              Grid View
            </button>
            <button 
              className={`btn ${viewMode === 'list' ? 'btn-primary' : 'btn-secondary'}`}
              onClick={() => setViewMode('list')}
            >
              List View
            </button>
          </div>
        </div>
        
        <div className="grid grid-5" style={{ gridTemplateColumns: 'repeat(auto-fit, minmax(150px, 1fr))' }}>
          <div 
            className={`shelf-card ${selectedShelf === 'all' ? 'active' : ''}`}
            onClick={() => setSelectedShelf('all')}
            style={{ 
              cursor: 'pointer', 
              padding: '1rem', 
              textAlign: 'center',
              border: selectedShelf === 'all' ? '2px solid #667eea' : '2px solid transparent',
              borderRadius: '10px',
              transition: 'all 0.3s ease'
            }}
          >
            <div style={{ fontSize: '2rem', marginBottom: '0.5rem' }}>📚</div>
            <div style={{ fontWeight: 'bold' }}>All Books</div>
            <div style={{ color: '#666', fontSize: '0.9rem' }}>{books.length} books</div>
          </div>
          
          {bookshelves.map(shelf => (
            <div 
              key={shelf.id}
              className={`shelf-card ${selectedShelf === shelf.id ? 'active' : ''}`}
              onClick={() => setSelectedShelf(shelf.id)}
              style={{ 
                cursor: 'pointer', 
                padding: '1rem', 
                textAlign: 'center',
                border: selectedShelf === shelf.id ? '2px solid #667eea' : '2px solid transparent',
                borderRadius: '10px',
                transition: 'all 0.3s ease'
              }}
            >
              <div style={{ fontSize: '2rem', marginBottom: '0.5rem' }}>{shelf.icon}</div>
              <div style={{ fontWeight: 'bold' }}>{shelf.name}</div>
              <div style={{ color: '#666', fontSize: '0.9rem' }}>{shelf.count} books</div>
            </div>
          ))}
        </div>
      </div>

      {/* 3D Bookshelf */}
      <div className="bookshelf">
        <div style={{ 
          display: 'flex', 
          justifyContent: 'space-between', 
          alignItems: 'center',
          marginBottom: '1rem',
          color: 'white'
        }}>
          <h3 style={{ margin: 0 }}>{selectedShelf === 'all' ? 'All Books' : bookshelves.find(s => s.id === selectedShelf)?.name}</h3>
          <span>{filteredBooks.length} books</span>
        </div>
        
        <div className="bookshelf-grid">
          {filteredBooks.map(book => (
            <div key={book.id} className="virtual-book" style={{ position: 'relative' }}>
              <div style={{ 
                fontSize: '2rem', 
                marginBottom: '0.5rem',
                transform: 'rotateY(10deg)',
                transition: 'transform 0.3s ease'
              }}>
                {book.cover}
              </div>
              <div style={{ 
                fontSize: '0.8rem', 
                fontWeight: 'bold',
                textAlign: 'center',
                lineHeight: '1.2'
              }}>
                {book.title}
              </div>
              <div style={{ 
                fontSize: '0.7rem', 
                opacity: 0.8,
                textAlign: 'center'
              }}>
                {book.author}
              </div>
              
              {/* Progress Bar */}
              {book.progress > 0 && (
                <div style={{ 
                  position: 'absolute', 
                  bottom: '10px', 
                  left: '10px', 
                  right: '10px',
                  height: '4px',
                  background: '#333',
                  borderRadius: '2px',
                  overflow: 'hidden'
                }}>
                  <div style={{
                    height: '100%',
                    width: `${book.progress}%`,
                    background: getProgressColor(book.progress),
                    transition: 'width 0.3s ease'
                  }}></div>
                </div>
              )}
              
              {/* Rating Stars */}
              {book.rating > 0 && (
                <div style={{ 
                  position: 'absolute', 
                  top: '10px', 
                  right: '10px',
                  fontSize: '0.8rem'
                }}>
                  {'★'.repeat(book.rating)}
                </div>
              )}
            </div>
          ))}
        </div>
      </div>

      {/* Reading Stats */}
      <div className="card" style={{ marginTop: '2rem', padding: '2rem' }}>
        <h3 style={{ marginBottom: '1.5rem', textAlign: 'center' }}>Reading Statistics</h3>
        <div className="grid grid-4">
          <div style={{ textAlign: 'center' }}>
            <div style={{ fontSize: '2rem', fontWeight: 'bold', color: '#667eea' }}>
              {books.filter(b => b.progress === 100).length}
            </div>
            <div style={{ color: '#666' }}>Completed</div>
          </div>
          <div style={{ textAlign: 'center' }}>
            <div style={{ fontSize: '2rem', fontWeight: 'bold', color: '#ffc107' }}>
              {books.filter(b => b.progress > 0 && b.progress < 100).length}
            </div>
            <div style={{ color: '#666' }}>In Progress</div>
          </div>
          <div style={{ textAlign: 'center' }}>
            <div style={{ fontSize: '2rem', fontWeight: 'bold', color: '#dc3545' }}>
              {books.filter(b => b.progress === 0).length}
            </div>
            <div style={{ color: '#666' }}>To Read</div>
          </div>
          <div style={{ textAlign: 'center' }}>
            <div style={{ fontSize: '2rem', fontWeight: 'bold', color: '#28a745' }}>
              {Math.round(books.reduce((acc, book) => acc + book.progress, 0) / books.length)}%
            </div>
            <div style={{ color: '#666' }}>Average Progress</div>
          </div>
        </div>
      </div>

      {/* Quick Actions */}
      <div className="card" style={{ marginTop: '2rem', padding: '2rem', textAlign: 'center' }}>
        <h3 style={{ marginBottom: '1rem' }}>Quick Actions</h3>
        <div style={{ display: 'flex', gap: '1rem', justifyContent: 'center', flexWrap: 'wrap' }}>
          <Link to="/books" className="btn btn-primary">
            Add New Book
          </Link>
          <Link to="/reading-journey" className="btn btn-secondary">
            View Reading Journey
          </Link>
          <Link to="/gamification" className="btn btn-secondary">
            View Achievements
          </Link>
        </div>
      </div>
    </div>
  );
};

export default VirtualBookshelf;
